    private void mostrarTabla(List<Producto> lista) {
        DefaultTableModel tabla;
        tabla = (DefaultTableModel) tblDatos.getModel();
        tabla.setRowCount(0);
        for (Producto r : lista) {
            Object[] rowData = {r.getIdProducto(), r.getNombre(), r.getCategoria(),
                r.getPrecio(), r.getPrecioMayor(), r.getStock()};
            tabla.addRow(rowData);
        }
    }
